import { Component, Input } from '@angular/core';
import { APP_BASE_HREF, CommonModule } from '@angular/common';
import { RouterModule } from '@angular/router';
import { IHeader } from 'src/app/interfaces/header.component';

@Component({
  selector: 'app-header-4',
  standalone: true,
  imports: [CommonModule, RouterModule],
  templateUrl: './header-4.component.html',
  styleUrls: ['./header-4.component.scss'],
  providers: [{provide: APP_BASE_HREF, useValue: '/'}],
})
export class Header4Component {
  @Input() header!: IHeader;
  @Input () header__white : string | undefined

  headerSticky : boolean = true;
  showCart : boolean = false;
  showSidebar : boolean = false;
  showHomeDropdown : boolean = false;
  showCoursesDropdown : boolean = false;
  showBlogDropdown : boolean = false;
  showPagesDropdown : boolean = false;
}

styleUrlsData.data:
@use '../../../../assets/styles/abstract/index' as *;

.header{
	&__padding{
		padding-left: 245px;
		padding-right: 245px;
		@media #{$xxl}{
			padding-left: 70px;
			padding-right: 70px;
		}
		@media #{$xl}{
			padding-left: 20px;
			padding-right: 20px;
		}
		@media #{$lg}{
			padding: 20px;
		}
		@media #{$md}{
			padding: 20px;
		}
		@media #{$sm}{
			padding: 20px;
		}
		@media #{$xs}{
			padding: 20px;
		}
		&-2{
			@media #{$lg}{
				padding: 20px 0;
			}
			@media #{$md}{
				padding: 20px 0;
			}
			@media #{$sm}{
				padding: 20px 0;
			}
			@media #{$xs}{
				padding: 20px 0;
			}
		}
	}
	&__shadow{
		@include box-shadow(0px 30px 40px 0px rgba(1, 11, 60, 0.06));
	}
	&__area{
		&.sticky{
            position: fixed;
            top: 0;
            left: 0;
            width: 100%;
            background: var(--bs-white);
            z-index: 99;
            animation: 300ms ease-in-out 0s normal none 1 running fadeInDown;
            -webkit-animation: 300ms ease-in-out 0s normal none 1 running fadeInDown;
            display: block;
            @include box-shadow(0px 10px 20px 0px rgba(8, 0, 42, 0.08));

			& .header__search{
				& input{
					background: var(--bs-dark);
				}
			}
			& .logo{
				& .logo-white{
					display: none;
				}
				& .logo-black{
					display: block;
				}
			}
			& .main-menu-3{
				& ul{
					& li{
						& a{
							color: var(--bs-dark);
							&::after{
								color: var(--bs-dark);
							}
							&::before{
								background: var(--bs-dark);
							}
						}
						& .submenu{
							& li{
								& a{
									&::before{
										background: var(--bs-primary);
									}
								}
							}
						}
					}
				}
			}
			& .header__search-2{
				& svg{
					& .st0{
						fill: var(--bs-dark);
					}
					& .st1{
						fill: var(--bs-dark)-2;
					}
				}
			}
			& .cat-menu{
				color: var(--bs-dark);
				&:hover{
					color: var(--bs-primary);
					& svg{
						& .cat-dot{
							fill: var(--bs-primary);
						}
					}
				}
				& svg{
					& .cat-dot{
						fill: var(--bs-dark);
					}
				}
			}
			& .header__category::after {
				background: rgba($color: var(--bs-dark), $alpha: .2);
			}
			& .sidebar-toggle-btn{
				& .line{
					background-color: var(--bs-primary);
				}
			}
        }
	}
	&__transparent{
		position: absolute;
		top: 0;
		left: 0;
		right: 0;
		width: 100%;
		z-index: 11;
	}
	&__white{
		& .cat-menu {
			color: var(--bs-white);
			& svg{
				& .cat-dot{
					fill:var(--bs-white);
				}
			}
			&:hover{
				color: var(--bs-white);
				& svg{
					& .cat-dot{
						fill: var(--bs-white);
					}
				}
			}
		}
		& .header__category::after {
			background: rgba($color: var(--bs-white), $alpha: .2);
		}
		& .main-menu{
			& ul{
				& li{
					& a{
						color: var(--bs-white);
					}
				}
			}
		}
		& .sidebar-toggle-btn{
			& .line{
				background-color: var(--bs-white);
			}
		}
		
	}
	&__category{
		position: relative;
		padding-left: 30px;
		margin-left: 30px;
		padding-top: 3px;
		&::after{
			position: absolute;
			content: '';
			left: 0;
			top: 60%;
			@include transform(translateY(-50%));
			width: 1px;
			height: 40px;
			background: var(--bs-dark);
		}
		& ul{
			& li{
				position: relative;
				& .cat-submenu{
					position: absolute;
					top: calc(100% + 30px);
					left: 0;
					width: 200px;
					padding: 17px 0;
					padding-bottom: 15px;
					background: var(--bs-white);
					@include box-shadow(0px 10px 20px 0px rgba(8, 0, 42, 0.08));
					@include border-radius(4px);
					z-index: 1;
					visibility: hidden;
					opacity: 0;
					@include transition(.3s);
					& li{
						display: block;
						margin-right: 0;
						& > a{
							width: 100%;
							display: block;
							padding: 3px 30px;
							text-transform: capitalize;
							color: $body-text-color;
							font-weight: 500;
							position: relative;
							&::after{
								right: 30px;
								@include transform(translateY(-50%) rotate(-90deg));
							}
						}
						&:hover{
							& > a{
								color: var(--bs-primary);
							}
						}
					}
				}
				&:hover{
					& .cat-submenu{
						visibility: visible;
						opacity: 1;
						top: calc(100% + 10px);
					}
				}
			}
		}
	}
}

.logo-black{
	display: none;
}

// category menu area start
.cat-menu{
	font-size: 16px;
	font-weight: 500;
	color: var(--bs-dark);
	position: relative;
	&:hover{
		color: var(--bs-primary);
		& svg{
			& .cat-dot{
				fill: var(--bs-primary);
			}
		}
	}
	& svg{
		width: 16px;
		height: 16px;
		& .cat-dot{
			fill: var(--bs-dark);
		}
	}
	& span{
		padding-left: 14px;
		line-height: 1;
		display: inline-block;
		padding-top: 3px;
	}
}


// main menu css
.main-menu{
	& ul{
		& li{
			display: inline-block;
			position: relative;
			&:not(:first-child){
				margin-left: 40px;
				@media #{$xxl}{
					margin-left: 30px;
				}
				@media #{$xl}{
					margin-left: 10px;
				}
			}
			& a{
				display: inline-block;
				font-size: 16px;
				font-weight: 500;
				color: var(--bs-dark);
				padding: 25px 0;
				text-transform: capitalize;
			}
			&.has-dropdown{
				& > a{
                    position: relative;
                }
			}
			&:hover{
				& > a{
					color: var(--bs-primary);
					&::after{
						color: var(--bs-primary);
					}
				}
				& .submenu{
					top: 100%;
					visibility: visible;
					opacity: 1;
				}
			}
			& .submenu{
                position: absolute;
                top: 120%;
                left: 0;
                width: 200px;
                background: var(--bs-white);
                z-index: 99;
                @include transition(.3s);
                visibility: hidden;
                opacity: 0;
                @include box-shadow(0px 30px 70px 0px rgba(11, 6, 70, 0.08));
                & li{
                    display: block;
                    width: 100%;
					margin: 0;
                    &:not(:last-child){
                        border-bottom: 1px solid rgba(0,0,0,0.10);
                    }
                    & a{
                        padding: 10px 25px;
                        font-size: 13px;
                        position: relative;
                        z-index: 1;
						color: var(--bs-dark);
						width: 100%;
                        &::before{
                            position: absolute;
                            content: '';
                            top: 0;
                            left: auto;
                            right: 0;
                            width: 0;
                            height: 100%;
                            background: var(--bs-primary);
                            z-index: -1;
                            
                        }
                        
                    }
                    & .submenu{
                        left: 120%;
                        top:0;
                        visibility: hidden;
                        opacity: 0;
                    }
                    &:hover{
                        & > a{
                            color: var(--bs-white);
                            &::after{
                                color: var(--bs-white);
                            }
                            &::before{
                                left: 0;
                                right: auto;
                                width: 100%;
                            }
                        }
                        & .submenu{
                            left: 100%;
                            visibility: visible;
                            opacity: 1;
                        }
                    }
                }
            }
		}
	}
}

.main-menu-2{
	& ul{
		& li{
			&:not(:first-child){
				margin-left: 35px;
				@media #{$xl}{
					margin-left: 20px;
				}
			}
			& .menu-tag{
                position: absolute;
                top: 13px;
                right: -4px;
                display: inline-block;
                height: 20px;
                line-height: 20px;
                padding: 0 7px;
                background: var(--bs-primary);
                color: var(--bs-white);
                @include border-radius(4px);
                font-size: 12px;
                text-transform: capitalize;
                @include box-shadow(0px 8px 20px 0px rgba(1, 23, 133, 0.3));

				@media #{$lg}{
					display: none;
				}
                &::after{
                    position: absolute;
                    content: '';
                    bottom: -5px;
                    left: 10px;
                    width: 0;
                    height: 0;
                    border-left: 5px solid var(--bs-primary);
                    border-top: 5px solid transparent;
                    border-bottom: 5px solid transparent;

                }
            }
		}
	}
}

.main-menu-3{
	& ul{
		& li{
			& a{
				color: var(--bs-white);
				position: relative;
				&::before{
					position: absolute;
					content: '';
					left: auto;
					right: 0;
					bottom: 38px;
					width: 0%;
					height: 1px;
					background: var(--bs-white);
				}
			}
			& .submenu{
				& li{
					& a{
						&::after{
							color: var(--bs-dark);
						}
					}
				}
			}
			&:hover{
				& a{
					color: var(--bs-white);
					&::before{
						width: 100%;
						left: 0;
						right: auto;
					}
				}
			}
		}
	}
}


/* sidebar css start */

.sidebar-toggle-btn{
    & .line{
        width: 30px;
        height: 3px;
        background-color: var(--bs-primary);
        display: block;
        margin: 6px auto;
        -webkit-transition: all 0.3s ease-in-out;
        -o-transition: all 0.3s ease-in-out;
        transition: all 0.3s ease-in-out;
    }
	&-white{
		& .line{
			background: var(--bs-white);
		}
	}
    &:hover{
        cursor: pointer;
        & .line{
            -webkit-transform: rotate(-30deg);
            -ms-transform: rotate(-30deg);
            -o-transform: rotate(-30deg);
            -moz-transform: rotate(-30deg);
            transform: rotate(-30deg);

            &:nth-child(1){
                width: 10px;
            }
            &:nth-child(2){
                width: 20px;
            }
        }
    }
}

.sidebar{
    &__area{
        position: fixed;
        right: -340px;
        top: 0;
        width: 320px;
        height: 100%;
        background: var(--bs-white)  none repeat scroll 0 0;
        overflow-y: scroll;
        @include box-shadow(-5px 0 20px -5px rgba(0, 0, 0, 0.5));
        -webkit-transition: all .3s cubic-bezier(0.785, 0.135, 0.15, 0.86);
        -moz-transition: all .3s cubic-bezier(0.785, 0.135, 0.15, 0.86);
        transition: all .3s cubic-bezier(0.785, 0.135, 0.15, 0.86);
        z-index: 9999;
        &.sidebar-opened{
            right: 0px;
        }
    }
    &__wrapper{
        position: relative;
        padding: 30px;
    }
    &__close{
        position: absolute;
        top: 25px;
        right: 80px;
        &-btn{
            transition: all 450ms cubic-bezier(.4,.25,.3,1.3);
            width: 40px;
            height: 40px;
            position: absolute;
            text-align: center;
            top: 0;
            left: 50%;
            transform: translateX(-50%);
            display: flex;
            justify-content: center;
            align-items: center;
            color: var(--bs-dark);
            border: 1px solid #2a3a57;
            border-radius: 50%;
            cursor: pointer;
            overflow: hidden;
            background: transparent;
            z-index: 99;
            &:focus{
                border: 1px solid var(--bs-dark);
            }
            & span{
                transition: all 400ms cubic-bezier(.4,.25,.3,1.3) 100ms;
                position: absolute;
                top: 54%;
                left: 50%;
                font-size: 14px;
                font-weight: 500;
                line-height: 40px;
                vertical-align: middle;
                &:first-of-type{
	                transform: translate(-50%,-50%);
	                -webkit-transform: translate(-50%,-50%);
	                -moz-transform: translate(-50%,-50%);
	                -ms-transform: translate(-50%,-50%);
	                -o-transform: translate(-50%,-50%);
                }
                &:last-of-type{
                    text-transform: uppercase;
                    transform: translate(-50%,50%);
                    -webkit-transform: translate(-50%,50%);
                    -moz-transform: translate(-50%,50%);
                    -ms-transform: translate(-50%,50%);
                    -o-transform: translate(-50%,50%);
                }
            }
            &:hover{
                width: 100px;
	            border-radius: 0;
                & span:first-of-type{
                    transform: translate(-50%,-150%);
                    -webkit-transform: translate(-50%,-150%);
                    -moz-transform: translate(-50%,-150%);
                    -ms-transform: translate(-50%,-150%);
                    -o-transform: translate(-50%,-150%);
                }
                & span:last-of-type{
                    transform: translate(-50%,-50%);
                    -webkit-transform: translate(-50%,-50%);
                    -moz-transform: translate(-50%,-50%);
                    -ms-transform: translate(-50%,-50%);
                    -o-transform: translate(-50%,-50%);
                }
            }
        }
    }
	&__search{
		& input{
			width: 100%;
			height: 50px;
			line-height: 20px;
			@include border-radius(4px);
			background: var(--bs-dark);
			outline: none;
			border: 1px solid var(--bs-white);
			font-size: 16px;
			padding: 25px 65px 20px 55px;

			&::placeholder{
				color: $e-text-1;
			}
			&:focus{
				border-color: var(--bs-primary);
			}
		}
		& button{
			background: transparent;
			position: absolute;
			top: 15px;
			left: 24px;
			& i{
				&::after{
					color: var(--bs-dark);
					opacity: 1;
				}
				&::before{
					color: $e-text-1;
				}
			}
		}
	}
	&__cart{
		&-icon{
			display: inline-block;
			& svg{
				width: 20px;
				height: 19px;
				& .st0{
					fill:none;
					stroke:var(--bs-dark);
					stroke-width:2;
					stroke-linecap:round;
					stroke-linejoin:round;
				}

			}
		}
		& a{
			position: relative;
			font-size: 17px;
			color: var(--bs-dark);
			& .cart-item{
				position: absolute;
				top: -10px;
				right: -10px;
				display: inline-block;
				width: 20px;
				height: 20px;
				line-height: 22px;
				text-align: center;
				font-size: 14px;
				color: var(--bs-white);
				background: var(--bs-primary);
				@include border-radius(50%);
				font-weight: 500;
				@include box-shadow(0px 10px 20px 0px rgba(4, 23, 118, 0.3));
			}
			&:hover{
				& .cart-item{
					background: var(--bs-dark);
				}
			}
		}
	}
}





/* mean menu customize */
.mean-container a.meanmenu-reveal {
	display: none;
}
.mean-container .mean-nav {
	background: none;
	margin-top: 0;
}
.mean-container .mean-bar {
	padding: 0;
	min-height: auto;
	background: none;
}
.mean-container .mean-nav > ul {
	padding: 0;
	margin: 0;
	width: 100%;
	list-style-type: none;
	display: block !important;
}
.mean-container a.meanmenu-reveal {
	display: none !important;
}
.mean-container .mean-nav ul li a {
	width: 100%;
	padding: 10px 0;
	color: var(--bs-dark);
	border-top: 1px solid #bbbcbf;
	font-size: 13px;
    font-weight: 600;
}

.mean-container .mean-nav ul li a.mean-expand {
	margin-top: 0px;
	height: 15px;
	line-height: 14px;
	border-bottom: none;
	padding: 0;
	display: inline-block;
	width: 40px;
	height: 44px;
	line-height: 44px;
}
.mean-container .mean-nav ul li a.mean-expand:hover {
    color: var(--bs-dark);
}

.mean-container .mean-nav > ul > li:first-child > a{
	border-top: 0;
}



.mean-container .mean-nav ul li a.mean-expand.mean-clicked{
    color: var(--bs-dark);
}
.mean-container .mean-nav ul li a.mean-expand.mean-clicked i{
	transform: rotate(45deg);
	-webkit-transform: rotate(45deg);
	-moz-transform: rotate(45deg);
	-ms-transform: rotate(45deg);
    -o-transform: rotate(45deg);
    color: var(--bs-dark);
}

@media (max-width:991px){
	nav#mobile-menu {
		display: none;
	}
}

