.navbar-area {
    background-color: transparent;
    transition: 0.5s;
    position: fixed;
    z-index: 999;
    left: 0;
    top: 0;
    width: 100%;
    height: auto;

    &.is-sticky {
        position: fixed;
        top: 0;
        left: 0;
        width: 100%;
        z-index: 999;
        box-shadow: 0 2px 28px 0 rgba(0, 0, 0, 0.06);
        background: var(--bs-light) !important;
        animation: 500ms ease-in-out 0s normal none 1 running fadeInDown;
        transition: 0.5s;
    }
}
.search-overlay {
    display: none;

    &.search-popup {
        position: absolute;
        top: 100%;
        width: 300px;
        right: 0;
        background: var(--bs-light);
        z-index: 2;
        padding: 20px;
        box-shadow: 0px 0px 15px 0px rgba(0, 0, 0, 0.1);
        margin-top: 25px;

        .search-form {
            position: relative;

            .search-input {
                display: block;
                width: 100%;
                height: 50px;
                line-height: initial;
                border: 1px solid #eeeeee;
                color: var(--bs-dark);
                outline: 0;
                transition: 0.5s;
                padding: {
                    top: 4px;
                    left: 10px;
                }
                &:focus {
                    border-color: var(--bs-primary);
                }
            }
            .search-button {
                position: absolute;
                right: 0;
                top: 0;
                height: 50px;
                background: transparent;
                border: none;
                width: 50px;
                outline: 0;
                color: var(--bs-info);
                transition: 0.5s;
                padding: 0;

                &:hover, &:focus {
                    color: var(--bs-primary);
                }
            }
        }
    }
}
// Mobile and iPad Navbar
@media only screen and (max-width: 991px) {
    .navbar-area {
        padding: {
            top: 20px;
            bottom: 20px;
        }
        &.is-sticky {
            border-bottom: none;
            box-shadow: 0 7px 13px 0 rgba(0, 0, 0, .1);
            padding: {
                top: 15px;
                bottom: 15px;
            }
        }
    }
    .exto-responsive-nav {
        display: block;
        .exto-responsive-menu {
            position: relative;
            &.mean-container {
                .mean-nav {
                    ul {
                        font-size: 14px;

                        li {
                            a {
                                font-size: 15px;
                                
                                &.active {
                                    color: var(--bs-primary);
                                }
                            }
                            li {
                                a {
                                    font-size: 15px;
                                }
                            }
                        }
                    }
                }
                .navbar-nav {
                    overflow-y: scroll;
                    height: 336px;
                    box-shadow: 0 7px 13px 0 rgba(0, 0, 0, .1);
                }
                .others-options {     
                    display: none;
                }
            }
        }
        .mean-container {
            a {
                &.meanmenu-reveal {
                    color: var(--bs-dark);

                    span {
                        background: var(--bs-dark);
                    }
                }
            }
        }
        .logo {
            position: relative;
            width: 50%;
            z-index: 999;
        }
    }
    .exto-nav {
        display: none;
    }
}

